export interface IGovProxyGovProxy {
  new (
    oracleAuthPolicy: string,
    proposalAuthPolicy: string,
    gPowerPolicy: string,
    gtPolicy: string,
  ): Validator;
  redeemer: {
    deadline: bigint;
    proposalInIx: bigint;
    emissionOracleRefInIx: bigint;
  };
}

export const GovProxyGovProxy = Object.assign(
  function (
    oracleAuthPolicy: string,
    proposalAuthPolicy: string,
    gPowerPolicy: string,
    gtPolicy: string,
  ) {
    return {
      type: "PlutusV2",
      script: applyParamsToScript(
        "",
        [oracleAuthPolicy, proposalAuthPolicy, gPowerPolicy, gtPolicy],
        {
          "dataType": "list",
          "items": [{ "dataType": "bytes" }, { "dataType": "bytes" }, {
            "dataType": "bytes",
          }, { "dataType": "bytes" }],
        },
      ),
    };
  },
  {
    redeemer: {
      "title": "Redeemer",
      "anyOf": [{
        "title": "Redeemer",
        "dataType": "constructor",
        "index": 0,
        "fields": [{ "dataType": "integer", "title": "deadline" }, {
          "dataType": "integer",
          "title": "proposalInIx",
        }, { "dataType": "integer", "title": "emissionOracleRefInIx" }],
      }],
    },
  },
) as unknown as IGovProxyGovProxy;

